import React, { useState } from 'react';
import { supabase } from '../lib/supabaseClient';
import emailjs from '@emailjs/browser';
import { PORTALES, generateCancelCode, isDateValid } from '../utils/validators';

const ReservationForm = ({ selectedDate, selectedSlot, onSuccess, onCancel }) => {
  const [formData, setFormData] = useState({
    nombre: '',
    apellidos: '',
    portal: '',
    piso: '',
    letra: '',
    movil: '',
    correo: ''
  });
  const [loading, setLoading] = useState(false);

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const validateForm = () => {
    const { nombre, apellidos, portal, piso, letra, movil, correo } = formData;

    if (!nombre || !apellidos || !portal || !piso || !letra || !movil || !correo) {
      alert('Todos los campos son obligatorios');
      return false;
    }

    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    if (!emailRegex.test(correo)) {
      alert('El correo electrónico no es válido');
      return false;
    }

    const phoneRegex = /^[0-9]{9,}$/;
    if (!phoneRegex.test(movil)) {
      alert('El teléfono móvil debe tener al menos 9 dígitos');
      return false;
    }

    return true;
  };

  const checkExistingReservations = async () => {
  const { portal, piso, letra } = formData;

  try {
    // Verificar mismo día - CUALQUIER SLOT
    const {  todayReservations, error: todayError } = await supabase
      .from('reservas')
      .select('*')
      .eq('fecha', selectedDate)
      .eq('portal', portal)
      .eq('piso', piso)
      .eq('letra', letra)
      .eq('status', 'activa');

    if (todayError) throw todayError;

    if (todayReservations && todayReservations.length > 0) {
      alert(`Ya tienes una reserva para este día a las ${todayReservations[0].hora_inicio}. No puedes reservar dos veces el mismo día.`);
      return false;
    }

    // Verificar día siguiente - CUALQUIER SLOT
    const nextDay = new Date(selectedDate);
    nextDay.setDate(nextDay.getDate() + 1);
    const nextDayStr = nextDay.toISOString().split('T')[0];

    const {  nextDayReservations, error: nextDayError } = await supabase
      .from('reservas')
      .select('*')
      .eq('fecha', nextDayStr)
      .eq('portal', portal)
      .eq('piso', piso)
      .eq('letra', letra)
      .eq('status', 'activa');

    if (nextDayError) throw nextDayError;

    if (nextDayReservations && nextDayReservations.length > 0) {
      alert(`Ya tienes una reserva para el día siguiente (${nextDayStr}) a las ${nextDayReservations[0].hora_inicio}. No se permiten reservas en días consecutivos.`);
      return false;
    }

    // Verificar día anterior - CUALQUIER SLOT
    const prevDay = new Date(selectedDate);
    prevDay.setDate(prevDay.getDate() - 1);
    const prevDayStr = prevDay.toISOString().split('T')[0];

    const {  prevDayReservations, error: prevDayError } = await supabase
      .from('reservas')
      .select('*')
      .eq('fecha', prevDayStr)
      .eq('portal', portal)
      .eq('piso', piso)
      .eq('letra', letra)
      .eq('status', 'activa');

    if (prevDayError) throw prevDayError;

    if (prevDayReservations && prevDayReservations.length > 0) {
      alert(`Tienes una reserva el día anterior (${prevDayStr}) a las ${prevDayReservations[0].hora_inicio}. No se permiten reservas en días consecutivos.`);
      return false;
    }

    // Verificar que el slot específico no esté ocupado por NADIE
    const {  slotReservation, error: slotError } = await supabase
      .from('reservas')
      .select('*')
      .eq('fecha', selectedDate)
      .eq('hora_inicio', selectedSlot)
      .eq('status', 'activa');

    if (slotError) throw slotError;

    if (slotReservation && slotReservation.length > 0) {
      alert('Este horario ya ha sido reservado por otra persona.');
      return false;
    }

    return true;
  } catch (error) {
    console.error('Error checking reservations:', error);
    throw error;
  }
};


  const sendConfirmationEmail = async (cancelCode) => {
    const cancelUrl = `${window.location.origin}${process.env.PUBLIC_URL}/#/cancelar?codigo=${cancelCode}`;

    const templateParams = {
      to_email: formData.correo,
      to_name: `${formData.nombre} ${formData.apellidos}`,
      fecha: selectedDate,
      hora: selectedSlot,
      portal: formData.portal,
      piso: formData.piso,
      letra: formData.letra,
      cancel_url: cancelUrl
    };

    try {
      await emailjs.send(
        process.env.REACT_APP_EMAILJS_SERVICE_ID,
        process.env.REACT_APP_EMAILJS_TEMPLATE_ID,
        templateParams,
        process.env.REACT_APP_EMAILJS_PUBLIC_KEY
      );
    } catch (error) {
      console.error('Error sending email:', error);
      throw error;
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!validateForm()) return;
    if (!isDateValid(selectedDate)) {
      alert('La fecha seleccionada no es válida');
      return;
    }

    setLoading(true);

    try {
      const canReserve = await checkExistingReservations();
      if (!canReserve) {
        setLoading(false);
        return;
      }

      const cancelCode = generateCancelCode();

      const { error } = await supabase
        .from('reservas')
        .insert([{
          fecha: selectedDate,
          hora_inicio: selectedSlot,
          nombre: formData.nombre,
          apellidos: formData.apellidos,
          portal: formData.portal,
          piso: formData.piso,
          letra: formData.letra,
          movil: formData.movil,
          correo: formData.correo,
          codigo_cancelacion: cancelCode,
          status: 'activa'
        }]);

      if (error) throw error;

      await sendConfirmationEmail(cancelCode);

      alert('¡Reserva realizada con éxito! Te hemos enviado un email de confirmación.');
      onSuccess();
    } catch (error) {
      console.error('Error creating reservation:', error);
      alert('Error al crear la reserva. Por favor, inténtalo de nuevo.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="reservation-form-container">
      <div className="form-header">
        <h2>Nueva Reserva</h2>
        <button className="close-btn" onClick={onCancel}>✕</button>
      </div>

      <div className="selected-slot-info">
        <p><strong>Fecha:</strong> {selectedDate}</p>
        <p><strong>Hora:</strong> {selectedSlot} - {selectedSlot.split(':')[0] === '20' ? '22:00' : `${parseInt(selectedSlot.split(':')[0]) + 1}:${selectedSlot.split(':')[1]}`}</p>
      </div>

      <form onSubmit={handleSubmit} className="reservation-form">
        <div className="form-row">
          <div className="form-group">
            <label>Nombre *</label>
            <input
              type="text"
              name="nombre"
              value={formData.nombre}
              onChange={handleChange}
              required
            />
          </div>

          <div className="form-group">
            <label>Apellidos *</label>
            <input
              type="text"
              name="apellidos"
              value={formData.apellidos}
              onChange={handleChange}
              required
            />
          </div>
        </div>

        <div className="form-row">
          <div className="form-group">
            <label>Portal *</label>
            <select
              name="portal"
              value={formData.portal}
              onChange={handleChange}
              required
            >
              <option value="">Selecciona</option>
              {PORTALES.map(p => (
                <option key={p} value={p}>{p}</option>
              ))}
            </select>
          </div>

          <div className="form-group">
            <label>Piso *</label>
            <input
              type="text"
              name="piso"
              value={formData.piso}
              onChange={handleChange}
              required
            />
          </div>

          <div className="form-group">
            <label>Letra *</label>
            <input
              type="text"
              name="letra"
              value={formData.letra}
              onChange={handleChange}
              maxLength="1"
              required
            />
          </div>
        </div>

        <div className="form-group">
          <label>Teléfono Móvil *</label>
          <input
            type="tel"
            name="movil"
            value={formData.movil}
            onChange={handleChange}
            placeholder="600123456"
            required
          />
        </div>

        <div className="form-group">
          <label>Correo Electrónico *</label>
          <input
            type="email"
            name="correo"
            value={formData.correo}
            onChange={handleChange}
            placeholder="tu@email.com"
            required
          />
        </div>

        <div className="form-actions">
          <button type="button" onClick={onCancel} className="btn-cancel">
            Cancelar
          </button>
          <button type="submit" className="btn-submit" disabled={loading}>
            {loading ? 'Reservando...' : 'Confirmar Reserva'}
          </button>
        </div>
      </form>
    </div>
  );
};

export default ReservationForm;
